import React from 'react';
import './App.css';
import InputTag from './component/setInput.js';

class  App extends React.Component  {

  state = {
    temp: undefined,
    city: undefined
  };
  getWeather = (e) => {
    e.preventDefault();
    console.log("Hello");
    //api.openweathermap.org/data/2.5/weather?q=Kiev,ua&appid=2f340c581eae47ad00eea18ed2125a92&units=metrics
  }

  render() {
    return (
      <div className="App">
        <InputTag methodWeather = {this.getWeather}/>
        <div id="container">
          <h3>Forecast</h3>
          <p>City:</p>
          <p>Temperature:</p>
        </div>
      </div>
    );
  }
}

export default App;
